      bcrypt.hash(user.password, salt, (error, hash) => {
        if (error) return next(error);
  
        // assigning hashed password
        user.password = hash;
        return next();
      });
    });
});

const UserModel = mongoose.model("Admin", UserSchema);
export default UserModel

